var map;
$(document).ready(function(){
  map = new GMaps({
    div: '#gmaps-markers',
    lat: -12.043333,
    lng: -77.028333
  });
  map.addMarker({
    lat: -12.043333,
    lng: -77.03,
    title: 'Lima',
    details: {
      database_id: 42,
      author: 'HPNeo'
    },
    click: function(e){
      if(console.log)
        console.log(e);
      alert('You clicked in this marker');
    }
  });
  map.addMarker({
    lat: -12.042,
    lng: -77.028333,
    title: 'Marker with InfoWindow',
    infoWindow: {
      content: '<p>HTML Content</p>'
    }
  });
});

/* Polygon */
var map;
$(document).ready(function(){
  map = new GMaps({
    div: '#gmaps-polygons',
    lat: -12.043333,
    lng: -77.028333
  });

  var path = [[-12.040397656836609,-77.03373871559225],
              [-12.040248585302038,-77.03993927003302],
              [-12.050047116528843,-77.02448169303511],
              [-12.044804866577001,-77.02154422636042]];

  polygon = map.drawPolygon({
    paths: path,
    strokeColor: '#BBD8E9',
    strokeOpacity: 1,
    strokeWeight: 3,
    fillColor: '#BBD8E9',
    fillOpacity: 0.6
  });
});

/* Overlay */
